const removeAllMarkers = () => {
  const markers = document.querySelectorAll(".marker");
  markers.forEach((marker) => {
    markersContainer.removeChild(marker);
  });
};

const onClickClearButton = () => {
  const allPersonsItems = document.querySelectorAll("#selectOptionPersons li");
  const allDatesItems = document.querySelectorAll("#selectOptionDates li");
  allPersonsItems.forEach((item) => item.classList.remove("active"));
  allDatesItems.forEach((item) => item.classList.remove("active"));
  // document.getElementById("toggleButtonPersons").innerHTML = "Выбрать человека";
  // document.getElementById("toggleButtonDates").innerHTML = "Выбрать месяц";
  selectedPerson = null;
  selectedDate = null;

  removeAllMarkers();

  personsMarkers.forEach((marker) => {
    createMarker(
      marker.x,
      marker.y,
      marker.marker,
      marker.description,
      marker.imageSrc,
      marker.description2,
      marker.person,
      marker.date
    );
  });
};

const onClickSearchButton = () => {
  if (selectedPerson && selectedDate) {
    removeAllMarkers();
    personsMarkers.filter((marker) => {
      if (
        marker.person === selectedPerson &&
        marker.date.includes(selectedDate)
      ) {
        createMarker(
          marker.x,
          marker.y,
          marker.marker,
          marker.description,
          marker.imageSrc,
          marker.description2,
          marker.person,
          marker.date
        );
      }
    });
  } else if (selectedPerson) {
    removeAllMarkers();
    personsMarkers.filter((marker) => {
      if (marker.person === selectedPerson) {
        createMarker(
          marker.x,
          marker.y,
          marker.marker,
          marker.description,
          marker.imageSrc,
          marker.description2,
          marker.person,
          marker.date
        );
      }
    });
  } else if (selectedDate) {
    console.log(selectedDate);
    removeAllMarkers();
    personsMarkers.filter((marker) => {
      if (marker.date.includes(selectedDate)) {
        createMarker(
          marker.x,
          marker.y,
          marker.marker,
          marker.description,
          marker.imageSrc,
          marker.description2,
          marker.person,
          marker.date
        );
      }
    });
  }
};

